import './App.css';
import React from 'react';

const tasks = ['Estudar', 'Almoçar', 'Correr no Parque'];
const Task = (value) => {
  return (
    <li>{value}</li>
  );
}

tasks.forEach((elen) => Task(elen));   

function App() {

  return (
    <div>
      <h2>Tarefas</h2>
      <ul>
        {Task}
      </ul>
    </div>
  );


}

export default App;
